gwf = Workflow()

##########################################################################################################################
# #########################################---- Retrieve Sequences ----###################################################
# ########################################################################################################################

def retrieve(path_in, file_list, output_folder, done):
    """Retrieve gene sequences from the species in the species list
      and create an unaligned multifasta for each gene."""
    inputs = []
    outputs = [done]
    options = {'cores': 10, 'memory': "20g", 'walltime': "1:00:00", 'account':"Coryphoideae"}

    spec = """
    source /home/owrisberg/miniconda3/etc/profile.d/conda.sh
    conda activate base

    cd {path_in}
    
    # Some species are manually removed at this step due to reasons such as double sampling from same specimen or doubtful species identification on specimen
    # These species are removed by editing the file_list.txt file which is found in the github folder.
    # You can also create this file list yourself if you wish control over which species are included in the pipeline.
    # This can be done by running the following command in the folder with the species files: ls *_trimmed.fasta > filelist.txt
    # You also need to change the path of the input file_list below where this function is called.

    python /home/owrisberg/Coryphoideae/github_code/coryphoideae_species_tree/samples2genes.py {file_list} {output_folder} > outstats.csv

    touch {done}

    """.format(path_in = path_in, file_list = file_list, output_folder = output_folder, done = done)

    return AnonymousTarget(inputs=inputs, outputs=outputs, options=options, spec=spec)


# ########################################################################################################################
# #############################################---- MAFT ----#############################################################
# ########################################################################################################################

def mafft(gene, path_in, path_out, done):#
    """Aligning all the sequences for each gene.
        Some Genes require a LOT of memory to not result in an Out Of Memory error (500g) but most will do fine with way less"""
    inputs = ["/home/owrisberg/Coryphoideae/work_flow/05_blacklisting/done/retrieve_done.txt"]
    outputs = [done,path_out+gene+"_aligned.fasta"] 
    options = {'cores': 1, 'memory': "500g", 'walltime': "50:00:00", 'account':"Coryphoideae"} # Most of the genes can run with 50g/24h but some require 500g/48h

    spec = """

    source /home/owrisberg/miniconda3/etc/profile.d/conda.sh
    conda activate mafft_env

    cd {path_in}

    #Aligning using mafft localpair which should be able to handle divergent sequences flanking an alignable region.
    mafft --globalpair --large --adjustdirectionaccurately --thread 1 {gene}.FNA > {path_out}{gene}_aligned.fasta
    
    touch {done}

    """.format(gene = gene, done = done, path_in = path_in, path_out=path_out)

    return AnonymousTarget(inputs=inputs, outputs=outputs, options=options, spec=spec)


# ########################################################################################################################
# #############################################---- Optrimal ----#########################################################
# ########################################################################################################################

def gt_trimming(path_in,done,gene, path_out):
    """ trimming all alignments for each of the GT values specified"""
    inputs = [path_in+gene+"_aligned.fasta", path_in+"/done/"+gene]
    outputs = [done,path_out+gene+"_aligned.fasta.old"] 
    options = {'cores': 10, 'memory': "20g", 'walltime': "04:00:00", 'account':"Coryphoideae"}

    spec="""
    #Running gaptrimming.sh 
    bash /home/owrisberg/Coryphoideae/github_code/coryphoideae_species_tree/gaptrimming.sh -g {gene}

    touch {done}

    """.format(path_in=path_in, done=done, gene=gene, path_out=path_out)

    return AnonymousTarget(inputs=inputs, outputs=outputs, options=options, spec=spec)

